"use strict";function changeIconLinkPreviewing(){var currentWindowHeight=window.innerHeight,enablePreviewHeight=700,iconLinkList=document.querySelector(".main-nav__icon-link-list"),iconLinkListHidden="main-nav__icon-link-list--hidden";if(currentWindowHeight<enablePreviewHeight){iconLinkList.classList.add(iconLinkListHidden)}else{iconLinkList.classList.remove(iconLinkListHidden)}}{var waitFontLoading=1500;setTimeout(function(){var wrapper=document.querySelector(".wrapper");wrapper.classList.remove("wrapper--hidden")},waitFontLoading);window.addEventListener("load",function(){changeIconLinkPreviewing()});var resizeQueue,resizeWait=300;window.addEventListener("resize",function(){clearTimeout(resizeQueue);setTimeout(function(){changeIconLinkPreviewing()},resizeWait)})}